import React from 'react';
import "../styles/EmployeeTable.css";
import employees from '../employees.json'
import Table from 'react-bootstrap/Table'



function EmployeeTable(props) {
  const {employees} = props; 
  const [sortedField,setSortedField] = React.useState(null)
  React.useMemo(() => {
  let sortedEmployees = [...employees]; 
  if (sortedField !== null){
  sortedEmployees.sort((a,b)=> {
    if (a[sortConfig.key] < b[sortConfig.key]) {
      return sortConfig.direction === 'ascending' ? -1:1; 
    }
    if (a[sortConfig.key] > b[sortConfig.key]){
      return sortConfig.direction === 'ascending' ? 1: -1; 
    }
    return 0; 
  })}; 
  return (
<Table>
  <thead>
  <tr>
        <th><button>Image</button></th>
       <th><button type="button" onClick={()=>setSortedField('name')}>Name</button></th>
       <th><button type="button" onClick={()=>setSortedField('phone')}>Phone</button></th>
       <th><button type="button" onClick={()=>setSortedField('email')}>Email</button></th>
        <th><button type="button" onClick={()=>setSortedField('dob')}>DOB</button></th>

        </tr>
  </thead>
  <tbody>
    {employees.map(employee => {
      return (
       <tr key="props.id">
       <td><img alt="employee badge"src={props.image} style={{width:"150px",height:"auto"}}/></td>
       <td>{props.name}</td>
       <td>{props.phone}</td>
       <td>{props.email}</td>
       <td>{props.dob}</td>

     </tr>
    )

    })}

        </tbody>
  </Table>


  )
})
}

export default EmployeeTable;
